import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Tiempo } from '../Tiempo';


@Component({
  selector: 'app-postTiempo',
  templateUrl: './postTiempo.component.html',
  styleUrls: ['./postTiempo.component.css']
})
export class PostTiempoComponent {
  fecha!: Date
  grados!: number
  pais!: string
  provincia!: string
  ciudad!: string
  
  constructor(private router: Router, private http: HttpClient) {
  }
  postTiemp() {

    const data: Tiempo = {
      fecha: this.fecha,
      grados: this.grados
    }

    let _headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'authorization': localStorage.getItem("Auth") || " "
    });

    this.http.post("/api/paises" + this.pais + "/provincias/" + this.provincia + "/ciudades/" + this.ciudad + "/tiempo", data, { headers: _headers }).subscribe(
      response => {
        console.log(response);
      },
      error => {
        console.log(error);
      }
    )
  }
}
<h6>Pais, Provincia y Ciudad a la que le queres agregar tiempo</h6>
<input type="text" [(ngModel)]="pais" placeholder="Pais" name="pais" required>
<br>
<input type="text" [(ngModel)]="provincia" placeholder="provincia" name="provincia" required>
<br>
<input type="text" [(ngModel)]="ciudad" placeholder="ciudad" name="ciudad" required>
<br>
<h6>Fecha y Grados</h6>
<input type="date" [(ngModel)]="fecha" placeholder="fecha" name="fecha" required>
<input type="number" [(ngModel)]="grados" placeholder="grados" name="grados" required>

<button (click)="postTiemp()"> submit </button>

<button routerLink="/tiempo"> Volver </button>
