import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Ciudad } from '../Ciudad';


@Component({
  selector: 'app-putCiudad',
  templateUrl: './putCiudad.component.html',
  styleUrls: ['./putCiudad.component.css']
})
export class PutCiudadComponent {
    pais!: string
    provincia!: string
    ciudad!: string
    tiempo: any;
  constructor(private router: Router, private http: HttpClient) {
  }

  goToPage(page: string) {
    this.router.navigate([page]);
  }

  putCiudad() {
      const data: Ciudad = {
      nombre: this.ciudad,
      registroTempreratura: this.tiempo
    }

    let _headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'authorization': localStorage.getItem("token") || " "
    });

    this.http.put("/api/pais" + this.pais + "/provincia/" + this.provincia + "/ciudad/" + this.ciudad, data, { headers: _headers }).subscribe(
      response => {
        console.log("Exito", response);
      },
      error => {
        console.log(":(", error);
      }
    )


  }

}
<h6>introducir los datos de la ciudad que quieres modificar</h6>
<input type="text" [(ngModel)]="pais" placeholder="Pais" name="pais" required>
<br>
<input type="text" [(ngModel)]="provincia" placeholder="provincia" name="provincia" required>
<br>
<input type="text" [(ngModel)]="ciudad" placeholder="ciudad" name="ciudad" required>
<br>
<button (click)="putCiudad()"> submit </button>

<button routerLink="/ciudad"> Volver </button>
