import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';


@Component({
  selector: 'app-borrarPais',
  templateUrl: './borrarPais.component.html',
  styleUrls: ['./borrarPais.component.css']
})
export class BorrarPaisComponent {
  pais!: string;


  constructor(private router: Router, private http: HttpClient) {
  }
  borrarPais() {
    let headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'Auth': localStorage.getItem("token") || " "
    });
 
    this.http.delete("/api/paises/" + this.pais).subscribe(
      response => {
        console.log(response);
        localStorage.setItem("token", String(response))
      },
      error => {
        console.log(error);
      }
    )
  }
}
